package com.orobator.android.gramophone.model.loaders;import android.content.Context;import android.database.Cursor;import android.os.Bundle;import com.orobator.android.gramophone.model.Album;import com.orobator.android.gramophone.model.SongDatabaseHelper;public class SongsListCursorLoader extends SQLiteCursorLoader {    public static final int ALL_SONGS_ID = 0;    public static final int ALBUM_SONGS_ID = 1;    private int mID;    private Bundle mArgs;    private SongDatabaseHelper mHelper;    public SongsListCursorLoader(Context context, int ID, Bundle args) {        super(context);        mID = ID;        mArgs = args;        mHelper = new SongDatabaseHelper(context);    }    @Override    protected Cursor loadCursor() {        switch (mID) {            case ALL_SONGS_ID:                return mHelper.querySongs();            case ALBUM_SONGS_ID:                String albumName = mArgs.getString(Album.KEY_ALBUM_NAME);                String albumArtist = mArgs.getString(Album.KEY_ALBUM_ARTIST);                Album album = new Album(albumName, albumArtist);                return mHelper.querySongsForAlbum(album);            default:                return mHelper.querySongs();        }    }}