package com.orobator.android.gramophone.view.fragments;import android.os.Bundle;import android.support.v4.app.FragmentManager;import android.support.v4.app.ListFragment;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ListView;import com.orobator.android.gramophone.R;import com.orobator.android.gramophone.model.Album;import com.orobator.android.gramophone.model.Library;import com.orobator.android.gramophone.view.adapters.AlbumCursorAdapter;public class AlbumsFragment extends ListFragment {    private static final String TAG = "AlbumsFragment";    private static Library library;    AlbumCursorAdapter mAdapter;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setRetainInstance(true);        Log.i(TAG, "onCreate");        library = Library.getLibrary(getActivity().getApplicationContext());        mAdapter = new AlbumCursorAdapter(getActivity().getApplicationContext(), library.getAlbums());        setListAdapter(mAdapter);    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.list_view_albums, parent, false);        Log.i(TAG, "onCreateView");        return view;    }    @Override    public void onListItemClick(ListView listView, View view, int position, long id) {        Album album = (Album) getListAdapter().getItem(position);        Bundle args = new Bundle();        args.putString(AlbumCursorAdapter.KEY_ALBUM_NAME, album.getAlbumName());        args.putString(AlbumCursorAdapter.KEY_ALBUM_ARTIST, album.getAlbumArtist());        AlbumSongsFragment albumSongsFragment = new AlbumSongsFragment();        albumSongsFragment.setArguments(args);        FragmentManager fm = getActivity().getSupportFragmentManager();        fm.beginTransaction()                .replace(R.id.content_frame, albumSongsFragment)                .addToBackStack(album.getAlbumName())                .commit();    }}